/**
* DEFAULT SLIDER STYLES
* Edit at your own risk!
*/
#MONETATE-ID {
  position: relative;
}

#MONETATE-ID [data-slider-mask] {
  overflow: hidden!important;
  position: relative!important;
  padding: 0!important;
  margin: 0!important;
}

#MONETATE-ID li {
  list-style: none;
}

#MONETATE-ID [data-slider] {
  padding: 0!important;
  margin: 0!important;
  display: block;
}

#MONETATE-ID [data-slide] {
  float: none;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider] {
  white-space: nowrap!important;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slide] {
  display: inline-block!important;
  white-space: normal;
  vertical-align: top;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slide] {
  display: block!important;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide] {
  position: absolute;
  top: 0;
  opacity: 0;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide][data-slide-active] {
  position: relative;
  z-index: 1;
  opacity: 1;

}

#MONETATE-ID [data-pagination]{
  margin: 10px;
  padding: 0;
  height: 18px; 
}

#MONETATE-ID [data-pagination-slide],
#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button]{
  cursor: pointer;
  z-index: 1;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  -ms-touch-action: pan-x;
  touch-action: pan-x;
}
#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider-mask],
#MONETATE-ID[data-slider-wrapper="fade"] [data-slider-mask] {
  -ms-touch-action: pan-y;
  touch-action: pan-y;
}

#MONETATE-ID [data-pagination-slide] * {
  pointer-events: none;
}

/** ----END DEFAULT STYLES---- */


/**
* CUSTOM SLIDER STYLING
*/
#MONETATE-ID [data-pagination="thumbnails"] [data-pagination-slide] {
  border-radius: 0;
  background: none;
  width: 40px;
  height: auto;
}

#MONETATE-ID [disabled] {
  opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide] {
  display: inline-block;
  width: 10px;
  height: 10px;
  background: #d8d8d8;
  margin: 4px;
  border-radius: 6px;
}

#MONETATE-ID [data-pagination-slide][data-slide-active] {
  opacity: 1;
  background-color: #00669e;
}

#MONETATE-ID [data-slide] > div {
  padding: 24px 20px;
  height: 100%;
}


#MONETATE-ID[data-slider-wrapper="vertical"] [data-prev-button] {
  top: 0;
  left: 50%;
  -webkit-transform: translateX(-50%);
  -ms-transform: translateX(-50%);
  transform: translateX(-50%);
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-next-button]{
  top: auto;
  bottom: 0;
  left: 50%;
  right: auto;
  -webkit-transform: translateX(-50%);
  -ms-transform: translateX(-50%);
  transform: translateX(-50%);
}

#MONETATE-ID [data-slider-mask] {
  display: inline-block;
  width: 100%;
}

#MONETATE-ID {
  text-align: center;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  height: 400px!important;
}

#MONETATE-ID [data-slide] img {
  max-height: 100%;
}

#MONETATE-ID [data-slide] a {
  text-decoration: none;
  color: #404041;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
  -ms-flex-direction: column;
  flex-direction: column;
  height: 100%;
  -ms-flex-pack: distribute;
  justify-content: space-around;
}

#MONETATE-ID [data-pagination*="dots"] [data-pagination-slide] {
  /* remove below for pagination numbers */
  text-indent: -9999px;
}


#MONETATE-ID img{
  max-width: 100%;
}

#MONETATE-ID .mt-productinfo{
  font-size: 0.875rem;
  font-weight: 400;
  margin-bottom: 0.25rem;
  -webkit-box-flex: 1;
  -ms-flex-positive: 1;
  flex-grow: 1;
}
#MONETATE-ID [data-slide]:hover > div {
  -webkit-box-shadow: 0px 0px 16px rgba(0, 0, 0, 0.2);
  box-shadow: 0px 0px 16px rgba(0, 0, 0, 0.2);
}

/* Flexbox for making the slider items all the same height */
#MONETATE-ID [data-slider-mask] {
  display: -webkit-inline-box !important;
  display: -ms-inline-flexbox !important;
  display: inline-flex !important;
}
#MONETATE-ID [data-slider] {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
}
#MONETATE-ID [data-slide] {
  display: inline-block !important;
  -ms-flex-negative: 0;
  flex-shrink: 0;
  float: none;
  padding: 5px;
  text-align: left;
}
#MONETATE-ID [data-slide] > div{
  height: 100%;
}

#MONETATE-ID .mt-slide-controls{
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  margin: 0 auto;
  -webkit-box-align: center;
  -ms-flex-align: center;
  align-items: center;
  -webkit-box-pack: center;
  -ms-flex-pack: center;
  justify-content: center;
  margin: 10px 0 20px;
}
#MONETATE-ID .mt-pricewrap {
  display:-webkit-box;
  display:-ms-flexbox;
  display:flex;
  -webkit-box-pack: justify;
  -ms-flex-pack: justify;
  justify-content: space-between;
}

#MONETATE-ID span.mt-pricenote {
  display: block;
  margin-top: 10px;
  text-align: left;
  color: #c3122f!important;
  font-weight: bold;
  display: none;
}

#MONETATE-ID .mt-memberprice {
  color: #c3122f!important;
  font-weight: bold;
}
#MONETATE-ID .mt-memberprice .mt-price {
  color: #c3122f!important;
  font-weight: bold;
}
#MONETATE-ID [data-mapstatus="map"] .mt-memberprice .mt-price {
  text-decoration: line-through;
}

#MONETATE-ID .mt-pricenote-modal{
  background: #fff;
  padding: 10px;
  border: 4px solid rgba(0,0,0,.2);
  position: absolute;
  top: 10%;
  width: 450px;
  left: calc(50% - 225px);
  display: none;
}
#MONETATE-ID .mt-pricenote-close{
  color: rgb(147, 149, 151);
  font-weight: bold;
  position: absolute;
  right: 10px;
  font-size: 18px;
}
#MONETATE-ID .mt-pricenote-close:hover{
  text-decoration: none;
}
#MONETATE-ID .mt-pricenote-modal h3{
  font-weight: 700;
  text-transform: uppercase;
  margin-top: 20px;
  font-size: 18px !important;
}
#MONETATE-ID .mt-pricenote-modal p{
  font-size: .875rem;
  padding-top: .75rem!important;
  padding-bottom: .75rem!important;
}

#MONETATE-ID .mt-pricewrap {
  font-size: 14px;
}

#MONETATE-ID span.mt-pricenote {
  font-size: 12px;
}

#MONETATE-ID p.mt-showdetails {
  color: #00669E;
  display: block;
  font-size: 0.875rem;
  font-weight: 600;
  line-height: 1.7;
  text-align: center;
  border: 2px solid #E3E3E5;
  padding: .6875rem 1.375rem .625rem;
  -webkit-transition: all 0.2s ease-in-out;
  -o-transition: all 0.2s ease-in-out;
  transition: all 0.2s ease-in-out;
  margin: 10px 0 0;
  height: 48px;
  -webkit-box-flex: 0;
  -ms-flex-positive: 0;
  flex-grow: 0;
  -ms-flex-negative: 0;
  flex-shrink: 0;
}
#MONETATE-ID .map-question{
  color: white;
  background: rgb(64,64,64);
  border-radius: 50%;
  width: 17px;
  height: 17px;
  text-align: center;
  margin-top: 20px;
  margin-left: 5px;
  display: inline-block;
}

#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
  background-color: #00669e;
  border: none;
  cursor: pointer;
  height: 30px;
  width: 30px;
  border-radius: 50%;
  line-height: 0;
  font-size: 0;
  color: transparent;
  padding: 0;
  position: relative;
  outline: 0;
  z-index: 1;
  min-width: 30px !important;
}

@media (min-width: 992px) {
  #MONETATE-ID [data-prev-button],
  #MONETATE-ID [data-next-button] {
    height:38px;
    width: 38px
  }
}

#MONETATE-ID [data-prev-button]:focus,
#MONETATE-ID [data-next-button]:focus,
#MONETATE-ID [data-prev-button]:hover,
#MONETATE-ID [data-next-button]:hover {
  background-color: #53b7e7;
  outline: 0;
  color: transparent
}
#MONETATE-ID [data-prev-button]::before,#MONETATE-ID [data-next-button]::before {
  background-repeat: no-repeat;
  background-size: 8px 12px;
  background-position: center;
  content: '';
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  z-index: 2;
  opacity: 1;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale
}

#MONETATE-ID [data-prev-button]::before {
  background-image: url(https://www.ganderoutdoors.com/assets/images/arrow-left-white.svg)
}
#MONETATE-ID [data-next-button]::before {
  background-image: url(https://www.ganderoutdoors.com/assets/images/arrow-right-white.svg)
}


/* ---------------------------------------------------------
HEADER STYLING
--------------------------------------------------------- */

#MONETATE-ID .mt-sliderTop {
  border-bottom: 2px solid #E3E3E5;
  margin-bottom: 1.25rem !important;
  padding: 0 0 .75rem;
  text-align: left;
}
#MONETATE-ID .mt-sliderTop h3 {
  display: inline-block !important;
}
#MONETATE-ID .mt-sliderTop .ml-3 {
  display: inline-block;
}

@media screen and (min-width: 992px) {
  #MONETATE-ID .mt-sliderTop {
    padding: 0 1.875rem .75rem;
  }
}


/* Align member pricing */
#MONETATE-ID .mt-productinfo {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
      -ms-flex-direction: column;
          flex-direction: column;
}

#MONETATE-ID .mt-productinfo > p:first-of-type {
  margin-bottom: 8px;
  margin-bottom: 0.5rem;
}

#MONETATE-ID .mt-pricinginfo {
  margin-top: auto;
}

#MONETATE-ID p.mt-showdetails {
  line-height: 1.1;
  padding: .75rem 1.375rem .625rem;
}
