/*---Default Styles---*/
/* Wrapper */
.mt_countdown {
  position: relative;
  display: block;
  width: 100%;
  height: auto;
  padding: 0;
  margin: 0 auto;
  color: #000;
  text-align: center;
  background-image: url({{BKG_URL}});
  background-repeat: no-repeat;
  background-position: cover;
}
/*Hides the plural parameter on any suffixes when counting down.*/
.mt_countdown .mt_hide_plural {
  display: none;
}



