value.content:
.master-wrapper .hero_img {
  display: none;
}

@media screen and (min-width: 769px) {
  .master-wrapper .hero_img {
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: calc(100% - 20px);
  }
}

@media screen and (min-width: 993px) {
  .master-wrapper .hero_img {
    background-position-y: center !important;
    height: 320px;
    width: 100%;
  }
}

.master-wrapper .signin_block {
  background: #00334B !important;
  display: block;
  height: 100%;
  padding-bottom: 30px;
  padding-left: 20px;
  padding-right: 20px;
  padding-top: 20px;
  position: relative;
  top: -44px;
  width: 100%;
}

@media screen and (min-width: 641px) {
  .master-wrapper .signin_block {
    margin-left: 0;
    min-height: 320px;
    top: 0;
  }
}

@media screen and (min-width: 769px) {
  .master-wrapper .signin_block {
    -ms-flex-line-pack: center;
        align-content: center;
    background: transparent !important;
    -webkit-box-sizing: border-box;
            box-sizing: border-box;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-wrap: wrap;
        flex-wrap: wrap;
    height: 320px;
    margin-left: 0;
    margin-top: 0;
    max-width: 940px;
    min-height: 0;
    padding-left: 34px;
    padding-top: 0;
    position: absolute !important;
    width: 100%;
  }
}

.master-wrapper .signin_block .block_title {
  font-size: 36px;
  padding: 17px 0 10px 0;
  text-align: left;
  width: 100%;
}

@media screen and (min-width: 769px) {
  .master-wrapper .signin_block .block_title {
    max-width: 100%;
  }
}

.master-wrapper .signin_block .block_subtitle {
  display: block;
  padding: 0 0 20px;
}

@media screen and (min-width: 769px) {
  .master-wrapper .signin_block .block_subtitle {
    max-width: 505px;
  }
}

.master-wrapper .signin_block .block_links {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: horizontal;
  -webkit-box-direction: normal;
      -ms-flex-direction: row;
          flex-direction: row;
  -ms-flex-wrap: wrap;
      flex-wrap: wrap;
  margin-left: 0;
  margin-top: 0;
  width: 100% !important;
}

@media screen and (min-width: 769px) {
  .master-wrapper .signin_block .block_links {
    -webkit-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    -ms-flex-wrap: nowrap;
        flex-wrap: nowrap;
    max-width: 100%;
  }
}

.master-wrapper .signin_block .block_links a {
  font-weight: bold;
}

.master-wrapper .signin_block .block_signin {
  -webkit-box-sizing: border-box;
          box-sizing: border-box;
  margin-left: 0;
  width: 100% !important;
}

@media screen and (min-width: 769px) {
  .master-wrapper .signin_block .block_signin {
    width: 160px !important;
  }
}

.master-wrapper .signin_block .block_link {
  -webkit-box-sizing: border-box;
          box-sizing: border-box;
  color: #fff;
  font-size: 18px;
  font-weight: normal !important;
  margin-top: 20px;
  text-align: left;
  text-decoration: underline;
}

@media screen and (min-width: 769px) {
  .master-wrapper .signin_block .block_link {
    margin-left: 40px;
    margin-top: 0;
  }
}
