[_nghost-c1] {
  max-width: 100% !important;
}

.flipp[_ngcontent-c1],
cvs-blob[_ngcontent-c1] {
  margin-left: auto !important;
  margin-right: auto !important;
  max-width: 964px !important;
  position: relative !important;
  width: 100% !important;
}

.mte-181012 {
  align-content: center;
  align-items: center;
  background: #F7F7F7;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  height: 278px;
  margin-bottom: 35px;
  margin-top: 25px;
  width: 100%;
}

.mte-181012__heading {
  display: block;
  margin-bottom: 20px;
  margin-left: auto;
  margin-right: auto;
  max-width: 964px;
  padding-left: 10px;
  padding-right: 10px;
  width: 100%;
}

.mte-181012__heading h2 {
  color: #000000;
  font-family: 'Helvetica-Bold';
  font-size: 32px;
}

.mte-181012__content {
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  justify-content: space-between;
  margin-left: auto;
  margin-right: auto;
  max-width: 964px;
  width: 100%;
}

.mte-181012__content .column {
  background: #fff;
  display: flex;
  flex-direction: row;
  height: 140px;
  justify-content: flex-start;
  margin-left: 10px;
  margin-right: 10px;
  max-width: 300px;
  padding-bottom: 31px;
  padding-left: 20px;
  padding-right: 31px;
  padding-top: 31px;
  width: 100%;
}

.mte-181012__content .column__icon {
  display: block;
  flex: 0 0 auto;
  margin-right: 10px;
}

.mte-181012__content .column__icon img {
  height: auto;
  max-width: 100%;
}

.mte-181012__content .column__text {
  display: flex;
  flex: 0 1 auto;
  flex-direction: column;
  margin-left: 10px;
}

.mte-181012__content .column__text h3 {
  color: #000000;
  font-family: 'Helvetica-Bold';
  font-size: 18px;
  line-height: 22px;
  margin-bottom: 5px;
}

.mte-181012__content .column__text p {
  color: #000000;
  font-family: 'Helvetica';
  font-size: 14px;
  line-height: 17px;
}

.mte-181012__content .column__text sup {
  color: #000000;
  font-family: 'Helvetica';
  font-size: 7px;
  line-height: 1;
}
