value.content:
.mte-181013 {
  align-content: center;
  align-items: center;
  background: #F7F7F7;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  height: 218px;
  margin-bottom: 10px;
  margin-top: 0;
  width: 100%;
}

.mte-181013__heading {
  display: block;
  margin-bottom: 10px;
  margin-left: auto;
  margin-right: auto;
  max-width: 964px;
  padding-left: 10px;
  padding-right: 10px;
  width: 100%;
}

.mte-181013__heading h2 {
  color: #000000;
  font-family: 'Helvetica-Bold';
  font-size: 14px;
}

.mte-181013__content {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-between;
  margin-left: auto;
  margin-right: auto;
  max-width: 964px;
  width: 100%;
}

.mte-181013__content .column {
  align-content: center;
  align-items: center;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  margin-left: auto;
  margin-right: auto;
  max-width: 100%;
  padding-bottom: 10px;
  padding-left: 10px;
  padding-right: 22px;
  padding-top: 10px;
  width: 100%;
}

.mte-181013__content .column:last-child {
  padding-bottom: 0;
}

.mte-181013__content .column__icon {
  display: flex;
  flex: 0 0 37px;
  margin-right: 10px;
}

.mte-181013__content .column__icon img {
  height: auto;
  max-height: 32px;
  max-width: 100%;
}

.mte-181013__content .column__text {
  display: flex;
  flex: 0 1 auto;
  flex-direction: column;
  margin-left: 10px;
}

.mte-181013__content .column__text h3 {
  color: #000000;
  font-family: 'Helvetica-Bold';
  font-size: 18px;
  line-height: 22px;
  margin-bottom: 5px;
}

.mte-181013__content .column__text p {
  color: #000000;
  font-family: 'Helvetica';
  font-size: 14px;
  line-height: 17px;
}

.mte-181013__content .column__text sup {
  color: #000000;
  font-family: 'Helvetica';
  font-size: 7px;
  line-height: 1;
}
