value.content:
#monetate_lightbox_mask {
  display: none !important;
}
.t120-remove{
    display:none
}
.t120-appear{
    display:visible
}
.t120-bracelet-container{
    position:absolute;
    top:50%;
    -webkit-transform:translateY(-50%);
    transform:translateY(-50%);
    margin:0 auto;
    left:0!important;
    right:0;
    height:320px!important;
    width:420px!important;
    border:2px solid #b3b3b7;
    background-color:#e0e0e0!important;
    z-index:99
}
.t120-titlebar{
    position:relative;
    left:20px;
    width:95%;
    margin-top:15px!important
}
.t120-bracelet1-wrapper{
    margin-left:20px;
    width:40%;
    height:200px
}
.t120-bracelet1-wrapper img{
    top:100px
}
.t120-bracelet1-wrapper h3{
    position:absolute;
    top:60px
}
.t120-bracelet2-wrapper{
    margin-left:20px
}
.t120-bracelet2-wrapper img{
    top:220px
}
.t120-bracelet2-wrapper h3{
    position:absolute;
    top:180px
}
.t120-bracelet-img{
    position:absolute;
    height:60px;
    width:110px;
    border:1px solid grey
}
.t120-size-table{
    position:absolute;
    font-family:inherit;
    font-size:14px;
    top:70px;
    left:170px;
    width:65%;
    border-collapse:separate;
    border-spacing:15px 0
}
.t120-size-table th{
    font-weight:900
}
.t120-size-table td{
    padding:0
}
.t120-table-spacer{
    height:10px
}
.t120-necklace-container{
    position:absolute;
    top:50%;
    -webkit-transform:translateY(-50%);
    transform:translateY(-50%);
    margin:0 auto;
    left:0!important;
    right:0;
    height:470px!important;
    width:390px!important;
    border:2px solid #b3b3b7;
    background-color:#e0e0e0!important;
    z-index:99
}
.t120-necklace-img{
    position:absolute;
    left:20px;
    width:260px;
    height:370px;
    border:1px solid grey
}
.t120-ring-container{
    position:absolute;
    top:50%;
    -webkit-transform:translateY(-50%);
    transform:translateY(-50%);
    margin:0 auto;
    left:0!important;
    right:0;
    height:360px!important;
    width:450px!important;
    border:2px solid #b3b3b7;
    background-color:#e0e0e0!important;
    z-index:99
}
.t120-ring-img{
    position:absolute;
    margin:0 auto;
    left:0;
    right:0;
    width:420px;
    height:230px;
    border:1px solid grey;
    top:110px
}
.t120-exit-button{
    position:absolute;
    top:5px;
    right:5px;
    height:40px;
    width:40px;
    border-radius:20px;
    color:#000;
    background-color:#fff;
    cursor:pointer;
    box-shadow:1px 1px 6px #888
}
.t120-exit-button p{
    text-align:center;
    font-weight:900;
    font-size:32px;
    -webkit-transform:translateY(-75%);
    transform:translateY(-75%)
}
.subHeadline{
    margin-left:20px;
    width:90%
}
@media screen and (max-width:512px){
    .t120-titlebar{
        position:relative;
        left:20px;
        width:95%;
        margin-top:20px!important;
        font-size:15px
    }
    .t120-ring-container{
        position:absolute;
        top:50%;
        -webkit-transform:translateY(-50%);
        transform:translateY(-50%);
        margin:0 auto;
        left:0!important;
        right:0;
        height:310px!important;
        width:310px!important;
        border:2px solid #b3b3b7;
        background-color:#e0e0e0!important;
        z-index:99;
        position:fixed
    }
    .t120-ring-img{
        position:absolute;
        margin:0 auto;
        left:0;
        right:0;
        width:280px;
        height:170px;
        border:1px solid grey;
        top:120px
    }
    .subHeadline{
        font-size:11px
    }
    .t120-necklace-container{
        position:absolute;
        top:50%;
        -webkit-transform:translateY(-50%);
        transform:translateY(-50%);
        margin:0 auto;
        left:0!important;
        right:0;
        height:470px!important;
        width:310px!important;
        border:2px solid #b3b3b7;
        background-color:#e0e0e0!important;
        z-index:99;
        position:fixed
    }
    .t120-bracelet-container{
        position:absolute;
        top:50%;
        -webkit-transform:translateY(-50%);
        transform:translateY(-50%);
        margin:0 auto;
        left:0!important;
        right:0;
        height:320px!important;
        width:340px!important;
        border:2px solid #b3b3b7;
        background-color:#e0e0e0!important;
        z-index:99;
        position:fixed
    }
    .t120-size-table{
        left:130px;
        font-size:13px;
        border-spacing:11px 0
    }
    .t120-size-table th{
        font-weight:900
    }
    .t120-size-table td{
        padding:0
    }
    .t120-table-spacer{
        height:20px
    }
}
@media screen and (max-width:340px){
    .t120-bracelet-container{
        position:absolute;
        top:50%;
        -webkit-transform:translateY(-50%);
        transform:translateY(-50%);
        margin:0 auto;
        left:0!important;
        right:0;
        height:320px!important;
        width:310px!important;
        border:2px solid #b3b3b7;
        background-color:#e0e0e0!important;
        z-index:99;
        position:fixed
    }
}
