value.content:
nav > ul > li > ul[aria-label="Accessories"] > div {
  display: none;
  z-index: 1;
}

@media screen and (min-width: 768px) {
  nav > ul > li > ul[aria-label="Accessories"] > div {
    display: block;
    margin-top: 8px;
    max-height: 1200px;
    max-width: 244px;
    min-height: 600px;
    min-width: 228px;
  }
}

@media screen and (min-width: 1025px) {
  nav > ul > li > ul[aria-label="Accessories"] > div {
    max-height: 504px;
    max-width: 244px;
    min-height: 504px;
    min-width: 228px;
  }
}
