value.content:
/* Hide existing content */
.bootstrap-main-container .row.collection-banner,
.bootstrap-main-container .row.collection-banner + div[class*="gap"],
.bootstrap-main-container .story-slider-ghost,
.bootstrap-main-container .storefront-story-slider,
.bootstrap-main-container .row + div[class*="gap"] {
  display: none !important;
}

/* Insert new content */
.mt_hp-insert {
  display: block;
  margin: 0 auto;
  padding-bottom: 60px;
  padding-top: 60px;
  position: relative;
}

.mt_hp-insert a.story-button {
  -moz-transition: all .2s ease-in;
  -o-transition: all .2s ease-in;
  -webkit-transition: all .2s ease-in;
  background-color: #E57177;
  border: solid #E57177 1px;
  border-radius: 0px;
  clear: both;
  color: #fff;
  cursor: pointer;
  display: inline-block;
  font-family: 'Montserrat', 'Verdana', 'Geneva', sans-serif;
  font-size: 14px;
  font-weight: 500;
  letter-spacing: 0.58px;
  line-height: normal;
  margin-left: auto;
  margin-right: auto;
  margin-top: auto;
  padding: 11px 37px;
  text-align: center;
  text-decoration: none;
  text-transform: uppercase;
  transition: all .2s ease-in;
}

.mt_hp-insert a.story-button:hover {
  background-color: #CC666D;
  color: #fff;
}

.mt_hp-insert h6.story-subheader {
  color: #6D6E71;
  display: block;
  font-family: 'Montserrat', 'Verdana', 'Geneva', sans-serif;
  font-size: 16px;
  letter-spacing: 0.5px;
  line-height: 16px;
  margin-top: 20px;
  position: relative;
  text-transform: uppercase;
}

.mt_hp-insert h2.story-header {
  color: #333;
  display: block;
  font-family: 'Oswald', 'Arial Narrow', 'Trebuchet MS', sans-serif;
  font-size: 30px;
  font-style: normal;
  letter-spacing: 1.75px;
  line-height: 30px;
  margin-bottom: 20px;
  margin-top: 5px;
  position: relative;
  text-transform: uppercase;
}

.mt_hp-insert .retina {
  display: none;
}

.mt_hp-insert .non-retina {
  display: block;
}

@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
  .mt_hp-insert .retina {
    display: block;
  }
  .mt_hp-insert .non-retina {
    display: none;
  }
}

.mt_hp-insert > .row {
  -ms-flex-line-pack: stretch;
      align-content: stretch;
  -webkit-box-align: stretch;
      -ms-flex-align: stretch;
          align-items: stretch;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: horizontal;
  -webkit-box-direction: normal;
      -ms-flex-direction: row;
          flex-direction: row;
  -ms-flex-wrap: wrap;
      flex-wrap: wrap;
  -webkit-box-pack: justify;
      -ms-flex-pack: justify;
          justify-content: space-between;
}

.mt_hp-insert .row > div[class*="col"] {
  -ms-flex-line-pack: stretch;
      align-content: stretch;
  -webkit-box-align: stretch;
      -ms-flex-align: stretch;
          align-items: stretch;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
      -ms-flex-direction: column;
          flex-direction: column;
  -ms-flex-wrap: wrap;
      flex-wrap: wrap;
  -webkit-box-pack: start;
      -ms-flex-pack: start;
          justify-content: flex-start;
}

.mt_hp-insert .row > div[class*="col"]:not(:last-child) {
  margin-bottom: 60px;
}

@media screen and (min-width: 992px) {
  .mt_hp-insert .row > div[class*="col"]:not(:last-child) {
    margin-bottom: 0;
  }
}
