@media (max-width: 1025px) {
  #MONETATE-ID {
    width: calc(100% - 20px) !important;
    margin-left: 10px;
  }
  .product-main-wrapper > #MONETATE-ID {
    width: calc(100% - 50px) !important;
    margin-left: 25px;
  }
  #MONETATE-ID [data-prev-button] {
    left: -30px !important;
  }
  #MONETATE-ID [data-next-button] {
    right: -30px !important;
  }
}

@media (max-width: 500px) {
  #MONETATE-ID [data-prev-button],
  #MONETATE-ID [data-next-button] {
    display: none !important;
  }
  #MONETATE-ID [data-slider-mask] {
    width: 100% !important;
  }
}

#MONETATE-ID .product-stars {
  width: 112px;
}

#MONETATE-ID .mt-title {
  height: 45px;
  overflow: hidden;
  color: rgb(42,42,42);
}
#MONETATE-ID .mt-sale-price {
  display: inline;
  color: #af0128;
  font-weight: bold;
  font-size: 16px;
}
#MONETATE-ID .mt-price {
  color: #bbb;
  text-decoration: line-through;
  font-size: 16px;
}
#MONETATE-ID [data-missing-keys*="salePrice"] .mt-price {
  display: block;
  color: #005b54;
  font-weight: bold;
  font-size: 16px;
  text-decoration: none;
}
#MONETATE-ID .mt-saving {
  padding-left: 5px;
}
#MONETATE-ID [data-missing-keys*="salePrice"] .mt-sale-price, 
#MONETATE-ID [data-missing-keys*="salePrice"] .mt-saving {
  display: none;
}
#MONETATE-ID .mt-rating {
  display: inline-block;
}
#MONETATE-ID h2 {
  text-align: left;
  margin-bottom: 0px;
}

/**
* DEFAULT SLIDER STYLES
* Edit at your own risk!
*/
#MONETATE-ID {
  position: relative;
  width: 100%;
}

#MONETATE-ID [data-slider-mask] {
  overflow: hidden!important;
  position: relative!important;
  padding: 0!important;
  margin: 0!important;
}

#MONETATE-ID li {
  list-style: none;
}

#MONETATE-ID [data-slider] {
  padding: 0!important;
  margin: 0!important;
  display: block;
}

#MONETATE-ID [data-slide] {
  float: none;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider] {
  white-space: nowrap!important;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slide] {
  display: inline-block!important;
  white-space: normal;
  vertical-align: top;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slide] {
  display: block!important;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide] {
  position: absolute;
  top: 0;
  opacity: 0;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide][data-slide-active] {
  position: relative;
  z-index: 1;
  opacity: 1;
}

#MONETATE-ID [data-pagination-slide],
#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button]{
  cursor: pointer;
  z-index: 1;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  touch-action: pan-x;
}
#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider-mask],
#MONETATE-ID[data-slider-wrapper="fade"] [data-slider-mask] {
  touch-action: pan-y;
}

#MONETATE-ID [data-pagination-slide] * {
  pointer-events: none;
}

/** ----END DEFAULT STYLES---- */


/**
* CUSTOM SLIDER STYLING
*/
#MONETATE-ID h2 {
  margin-bottom: 0px;
}

#MONETATE-ID [data-pagination="thumbnails"] [data-pagination-slide] {
  border-radius: 0;
  background: none;
  width: 40px;
  height: auto;
}

#MONETATE-ID [disabled] {
  opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide] {
  display: inline-block;
  width: 12px;
  height: 12px;
  background: #000;
  margin: 4px;
  border-radius: 6px;
  opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide][data-slide-active] {
  opacity: 1;
}



#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
  top: 45%;
  position: absolute;
  color: #000;
  height: 40px;
  width: 40px;
  line-height: 0;
  font-size: 0;
  cursor: pointer;
  background: hsla(0,0%,100%,.4);
  color: rgba(0,0,0,.6);
  padding: 0;
  border: none;
  outline: none;
  z-index: 50;
}
#MONETATE-ID [data-prev-button][aria-hidden],
#MONETATE-ID [data-next-button][aria-hidde] {
  opacity: 0.25;
}

#MONETATE-ID [data-prev-button] {
  left: -40px;
}
#MONETATE-ID [data-prev-button]:before {
  content: "";
}

#MONETATE-ID [data-next-button] {
  right: -40px;
}
#MONETATE-ID [data-next-button]:before {
  content: "";
}

#MONETATE-ID [data-prev-button]:before , #MONETATE-ID [data-next-button]:before {
  font-family: Material Icons;
  font-weight: 400;
  font-style: normal;
  display: inline-block;
  text-transform: none;
  letter-spacing: normal;
  word-wrap: normal;
  white-space: nowrap;
  direction: ltr;
  vertical-align: middle;
  text-rendering: optimizeLegibility;
  -webkit-font-feature-settings: "liga";
  font-feature-settings: "liga";
  font-size: 30px;
  line-height: 1;
  color: #000;
  opacity: .75;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-prev-button] {
  top: 0;
  left: 50%;
  transform: translateX(-50%);
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-next-button]{
  top: auto;
  bottom: 0;
  left: 50%;
  right: auto;
  transform: translateX(-50%);
}

#MONETATE-ID [data-slider-mask] {
  display: inline-block;
  width: calc(100% - 20px);
  margin-left: -10px !important;
}

#MONETATE-ID {
  text-align: center;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  height: 400px!important;
}

#MONETATE-ID [data-slide] img {
  max-height: 100%;
  width: 100%;
}

#MONETATE-ID [data-slide] a {
  text-decoration: none;
}

#MONETATE-ID [data-pagination*="dots"] [data-pagination-slide] {
  /* remove below for pagination numbers */
  text-indent: -9999px;
}

.page-cartPage .product.cart_product {
  border-bottom: none !important;
}
.page-cartPage .promo-item {
  border-top: 2px solid #005b54;
}
@media (min-width: 860px) {
  .page-cartPage #promo #MONETATE-ID {
    width: 150%;
  }
}
#basket-list-items-content {
  overflow: visible !important;
}
#product-listings__listings-items-wrapper > .content-banner > .paragraph-box {
  width: 100%;
}

/* Don't display stars if there is no rating */
.rating-stars.rating .product-stars {
  background: none;
}


#MONETATE-ID [data-slide] > div {
  margin: 0px 10px 0 10px;
  padding: 15px;
  height: 100%;
}
