/* ===================================================== */
/* DEFAULT SLIDER STYLES
/* Edit at your own risk!
/* ===================================================== */
#MONETATE-ID {
  position: relative;
}

#MONETATE-ID li {
  list-style: none;
}

#MONETATE-ID [data-slider-mask] {
  margin: 0 !important;
  overflow: hidden !important;
  padding: 0 !important;
  position: relative !important;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider-mask],
#MONETATE-ID[data-slider-wrapper="fade"] [data-slider-mask] {
  -ms-touch-action: pan-y;
      touch-action: pan-y;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  -ms-touch-action: pan-x;
      touch-action: pan-x;
}

#MONETATE-ID [data-slider] {
  display: block;
  margin: 0 !important;
  padding: 0 !important;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider] {
  white-space: nowrap !important;
}

#MONETATE-ID [data-slide] {
  float: none;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slide] {
  display: inline-block !important;
  vertical-align: top;
  white-space: normal;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slide] {
  display: block !important;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide] {
  opacity: 0;
  position: absolute;
  top: 0;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide][data-slide-active] {
  opacity: 1;
  position: relative;
  z-index: 1;
}

#MONETATE-ID [data-pagination-slide],
#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
  cursor: pointer;
  z-index: 1;
}

#MONETATE-ID [data-pagination-slide] * {
  pointer-events: none;
}

/* ===================================================== */
/* CUSTOM SLIDER STYES
/* ===================================================== */
#MONETATE-ID {
  background-color: #fafafa;
  clear: both;
  color: #191919;
  margin: 19px 2.5%;
  text-align: center;
}

#MONETATE-ID .mt_slider__container {
  clear: both;
  margin: 0 auto;
  padding: 0;
  position: relative;
}

#MONETATE-ID .mt_slider__header {
  display: inline-block;
  font-family: 'Saira Extra Condensed',sans-serif;
  font-size: 20px;
  font-weight: 500;
  line-height: 12px;
  margin: 0;
  margin-bottom: 25px;
  padding: 0;
  padding-bottom: 10px;
  padding-top: 10px;
  position: relative;
  text-transform: uppercase;
}

#MONETATE-ID .mt_slider__header::after {
  background: #00b663;
  bottom: -5px;
  content: "";
  height: 5px;
  left: 0;
  position: absolute;
  width: 100%;
}

#MONETATE-ID .mt_slider__wrapper {
  clear: both;
}

#MONETATE-ID [data-slider-mask] {
  display: inline-block;
  width: 100%;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  height: 400px !important;
}

#MONETATE-ID [data-slider] {
  -webkit-transform: none !important;
          transform: none !important;
  -webkit-transition: none !important;
  transition: none !important;
}

#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
  -moz-user-select: none;
  -ms-user-select: none;
  -o-user-select: none;
  -webkit-user-select: none;
  background: none;
  border: none;
  color: #000;
  display: none;
  font-size: 25px;
  padding: 0;
  position: absolute;
  top: 50%;
  user-select: none;
}

#MONETATE-ID [data-prev-button] {
  left: -30px;
}

#MONETATE-ID [data-next-button] {
  right: -30px;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-prev-button] {
  left: 50%;
  top: 0;
  -webkit-transform: translateX(-50%);
          transform: translateX(-50%);
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-next-button] {
  bottom: 0;
  left: 50%;
  right: auto;
  top: auto;
  -webkit-transform: translateX(-50%);
          transform: translateX(-50%);
}

#MONETATE-ID [data-pagination="thumbnails"] [data-pagination-slide] {
  background: none;
  border-radius: 0;
  height: auto;
  width: 40px;
}

#MONETATE-ID [disabled] {
  opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide] {
  background: #000;
  border-radius: 6px;
  display: inline-block;
  height: 12px;
  margin: 4px;
  opacity: 0.5;
  width: 12px;
}

#MONETATE-ID [data-pagination-slide][data-slide-active] {
  opacity: 1;
}

#MONETATE-ID [data-pagination*="dots"] [data-pagination-slide] {
  /* remove below for pagination numbers */
  text-indent: -9999px;
}

#MONETATE-ID [data-slide] > div {
  -webkit-box-shadow: -1px 0 0 #fff;
  background: 0;
  box-shadow: -1px 0 0 #fff;
  display: inline-block;
  padding: 0;
  position: relative;
  vertical-align: top;
  width: 100%;
}

#MONETATE-ID [data-slide] img {
  max-width: 100%;
}

#MONETATE-ID [data-slide] a {
  text-decoration: none;
}

#MONETATE-ID .mt_slide__text {
  clear: both;
  color: #191919;
  display: block;
  font-size: 11px;
  font-weight: 700;
  margin: 20px 5% 14px;
  text-align: center;
}

#MONETATE-ID .mt_slide__brand {
  display: block;
}

#MONETATE-ID .mt_slide__title {
  color: #191919;
  display: block;
  font-weight: 400;
  margin-bottom: 5px;
  margin-top: 5px;
  text-decoration: none;
}

#MONETATE-ID .mt_slide__price {
  display: block;
  margin-bottom: 5px;
  margin-top: 5px;
}

#MONETATE-ID .mt_slide__price--sale {
  display: none;
}
