value.content:
#mt-promo-drawer {
  border-radius: 15px 15px 0 0;
  bottom: 0;
  color: #fff;
  font-family: 'Open Sans', sans-serif;
  left: 50%;
  max-width: 1137px;
  opacity: 1;
  position: fixed;
  touch-action: none;
  -webkit-transform: translate(-50%, 0%);
          transform: translate(-50%, 0%);
  -webkit-transform-style: preserve-3d;
          transform-style: preserve-3d;
  -webkit-transition: all 1s ease;
  transition: all 1s ease;
  width: 100%;
  z-index: 9999;
}

#mt-promo-drawer .mt-title-wrapper {
  -webkit-box-align: center;
  -webkit-align-items: center;
          align-items: center;
  background: #242424;
  border-radius: 15px 15px 0 0 !important;
  box-sizing: border-box;
  cursor: pointer;
  display: -webkit-box;
  display: -webkit-flex;
  display: flex;
  -webkit-box-orient: horizontal;
  -webkit-box-direction: reverse;
  -webkit-flex-direction: row-reverse;
          flex-direction: row-reverse;
  -webkit-flex-wrap: nowrap;
          flex-wrap: nowrap;
  height: 42px !important;
  -webkit-box-pack: center;
  -webkit-justify-content: center;
          justify-content: center;
  left: 50%;
  overflow: hidden;
  position: relative;
  text-transform: uppercase;
  -webkit-transform: translateX(-50%);
          transform: translateX(-50%);
  -webkit-transform-style: preserve-3d;
          transform-style: preserve-3d;
  vertical-align: bottom;
  white-space: nowrap;
  width: 398px;
}

#mt-promo-drawer .mt-title-wrapper .mt-expand-btn {
  -webkit-box-align: center;
  -webkit-align-items: center;
          align-items: center;
  border: 0;
  display: -webkit-inline-box;
  display: -webkit-inline-flex;
  display: inline-flex;
  height: 24px;
  -webkit-box-pack: center;
  -webkit-justify-content: center;
          justify-content: center;
  margin-left: 10px;
  -webkit-transform: rotateX(180deg);
          transform: rotateX(180deg);
  -webkit-transform-style: preserve-3d;
          transform-style: preserve-3d;
  -webkit-transition: -webkit-transform 300ms ease-in-out;
  transition: -webkit-transform 300ms ease-in-out;
  transition: transform 300ms ease-in-out;
  transition: transform 300ms ease-in-out, -webkit-transform 300ms ease-in-out;
  width: 24px;
}

#mt-promo-drawer .mt-title-wrapper .mt-expand-btn svg {
  fill: #fff;
  height: 14px;
  width: 14px;
}

#mt-promo-drawer .mt-title-wrapper .mt-expand-title {
  border: none;
  font-size: 14px;
  font-weight: 700;
  letter-spacing: 2px;
  line-height: 0;
  margin: 0;
  outline: none;
  padding: 0;
}

#mt-promo-drawer.mt-expanded .mt-title-wrapper .mt-expand-btn {
  -webkit-transform: rotateX(0deg);
          transform: rotateX(0deg);
  -webkit-transform-style: preserve-3d;
          transform-style: preserve-3d;
}

#mt-promo-drawer .mt-body-wrapper {
  background: #fff;
  color: #1f1f1f;
  height: auto !important;
  max-height: 0px;
  overflow: hidden;
  -webkit-transition: all 400ms ease-in-out;
  transition: all 400ms ease-in-out;
  width: 100%;
}

#mt-promo-drawer.mt-expanded .mt-body-wrapper {
  box-shadow: 0 0px 8px rgba(0, 0, 0, 0.5);
  max-height: 600px;
}

#mt-promo-drawer [data-slider-wrapper] {
  margin: 0 auto;
  max-width: 1440px;
  position: relative;
}

#mt-promo-drawer [data-slider-mask] {
  margin: 0 auto !important;
  overflow: hidden !important;
  padding: 0 !important;
  position: relative !important;
}

#mt-promo-drawer li {
  list-style: none;
}

#mt-promo-drawer img {
  max-width: 100%;
}

#mt-promo-drawer [data-slider] {
  display: block;
  margin: 0;
  padding: 0;
  padding-top: 20px;
}

#mt-promo-drawer [data-slide] {
  float: none;
}

#mt-promo-drawer [data-slider-wrapper="horizontal"] [data-slider] {
  white-space: nowrap !important;
}

#mt-promo-drawer [data-slider-wrapper="horizontal"] [data-slide] {
  display: inline-block !important;
  vertical-align: top;
  white-space: normal;
}

#mt-promo-drawer [data-prev-button],
#mt-promo-drawer [data-next-button] {
  cursor: pointer;
  position: absolute;
  z-index: 1;
}

#mt-promo-drawer [data-prev-button][disabled],
#mt-promo-drawer [data-next-button][disabled] {
  display: none;
}

#mt-promo-drawer [data-slider-wrapper="horizontal"] [data-slider-mask] {
  touch-action: pan-y;
}

#mt-promo-drawer [data-prev-button],
#mt-promo-drawer [data-next-button] {
  -moz-user-select: none;
  -ms-user-select: none;
  -o-user-select: none;
  -webkit-user-select: none;
  -webkit-box-align: center;
  -webkit-align-items: center;
          align-items: center;
  background: none;
  border: none;
  display: -webkit-inline-box;
  display: -webkit-inline-flex;
  display: inline-flex;
  height: 36px;
  -webkit-box-pack: center;
  -webkit-justify-content: center;
          justify-content: center;
  margin: 0;
  outline: none;
  padding: 0;
  user-select: none;
  width: 36px;
}

#mt-promo-drawer [data-prev-button] svg,
#mt-promo-drawer [data-next-button] svg {
  fill: #000;
  height: 24px;
  width: 24px;
}

#mt-promo-drawer .mt-body-wrapper [data-prev-button],
#mt-promo-drawer .mt-body-wrapper [data-next-button] {
  top: 45%;
  -webkit-transform: translateY(-50%);
          transform: translateY(-50%);
  -webkit-transform-style: preserve-3d;
          transform-style: preserve-3d;
}

@media screen and (min-width: 1024px) {
  #mt-promo-drawer .mt-body-wrapper [data-prev-button],
  #mt-promo-drawer .mt-body-wrapper [data-next-button] {
    top: 48%;
  }
}

#mt-promo-drawer .mt-body-wrapper [data-prev-button] {
  left: 6px;
}

#mt-promo-drawer .mt-body-wrapper [data-next-button] {
  right: 6px;
}

#mt-promo-drawer .mt-view-all-offers {
  margin-bottom: 20px;
  margin-top: 15px;
  text-align: center;
  text-transform: uppercase;
}

#mt-promo-drawer .mt-view-all-offers a {
  color: #1f1f1f;
  font-size: 14px;
  font-weight: 700;
  letter-spacing: 1px;
  text-decoration: underline;
}

#mt-promo-drawer .mt-promo {
  font-size: 14px;
  font-weight: 700;
  line-height: 120%;
  padding: 0 10px;
  text-transform: uppercase;
}

#mt-promo-drawer .mt-promo a {
  color: #1f1f1f;
  cursor: pointer;
  text-decoration: none;
}

#mt-promo-drawer .mt-promo__title {
  background: #fff;
  color: #1f1f1f;
  padding: 4px 0;
}

#mt-promo-drawer .mt-promo__code {
  background: #fff;
  color: #1f1f1f;
  padding: 4px 0;
}
