/**
 * DEFAULT SLIDER STYLES
 * Edit at your own risk!
 */

#MONETATE-ID {
    position: relative;
    max-width: 140rem;
    margin: 0 auto;
}

#MONETATE-ID [data-slider-mask] {
    overflow: hidden!important;
    position: relative!important;
    padding: 0!important;
    margin: 0!important;
}

#MONETATE-ID li {
    list-style: none;
}

#MONETATE-ID [data-slider] {
    padding: 0!important;
    margin: 0!important;
    display: block;
}

#MONETATE-ID [data-slide] {
    float: none;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider] {
    white-space: nowrap!important;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slide] {
    display: inline-block!important;
    white-space: normal;
    vertical-align: top;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slide] {
    display: block!important;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide] {
    position: absolute;
    top: 0;
    opacity: 0;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide][data-slide-active] {
    position: relative;
    z-index: 1;
    opacity: 1;
}

#MONETATE-ID [data-pagination-slide],
#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
    cursor: pointer;
    z-index: 1;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
    touch-action: pan-x;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider-mask],
#MONETATE-ID[data-slider-wrapper="fade"] [data-slider-mask] {
    touch-action: pan-y;
}

#MONETATE-ID [data-pagination-slide] * {
    pointer-events: none;
}

/** ----END DEFAULT STYLES---- */

/**
 * CUSTOM SLIDER STYLING
 */

#MONETATE-ID [data-pagination="thumbnails"] [data-pagination-slide] {
    border-radius: 0;
    background: none;
    width: 40px;
    height: auto;
}

#MONETATE-ID [disabled] {
    opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide] {
    display: inline-block;
    width: 12px;
    height: 12px;
    background: #000;
    margin: 4px;
    border-radius: 6px;
    opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide][data-slide-active] {
    opacity: 1;
}

#MONETATE-ID [data-slide]>div {
    padding: 10px 10px 44px;
    height: 100%;
}

#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
    top: 45%;
    position: absolute;
    font-size: 25px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -o-user-select: none;
    user-select: none;
    padding: 0;
    background: none;
    color: #000;
}

#MONETATE-ID [data-prev-button] {
    left: 10px;
}

#MONETATE-ID [data-next-button] {
    right: 10px;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-prev-button] {
    top: 0;
    left: 50%;
    transform: translateX(-50%);
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-next-button] {
    top: auto;
    bottom: 0;
    left: 50%;
    right: auto;
    transform: translateX(-50%);
}

#MONETATE-ID [data-slider-mask] {
    display: inline-block;
    width: 100%;
}

#MONETATE-ID {
    text-align: left;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
    height: 400px!important;
}

#MONETATE-ID [data-slide] img {
    max-height: 100%;
}

#MONETATE-ID [data-slide] a {
    text-decoration: none;
    color: #575353;
}

#MONETATE-ID [data-pagination*="dots"] [data-pagination-slide] {
    /* remove below for pagination numbers */
    text-indent: -9999px;
}

#MONETATE-ID .mt-title,
#MONETATE-ID .mt-product-title {
    font-family: 'DINPro-Bold', 'Helvetica Neue', Helvetica, Arial, sans-serif;
}

#MONETATE-ID .mt-title {
    font-size: 1.8rem;
    font-weight: 500;
    text-align: center;
    margin-bottom: 22px;
}

#MONETATE-ID .mt-merch-item {
    border: 1px solid transparent;
}

#MONETATE-ID .mt-merch-item:hover {
    border-color: #e5e5e5;
}

#MONETATE-ID .mt-product-title {
    text-transform: uppercase;
    border-top: .2rem solid #e5e5e5;
    margin-top: 8px;
    padding-top: 16px;
    font-size: 1.2rem;
    color: #222;
}

#MONETATE-ID .mt-price-text {
    margin-top: 0.8em;
}

#MONETATE-ID .mt-price {
    text-decoration: line-through;
    color: #9e9e9e;
    margin-left: .8rem;
}

#MONETATE-ID .mt-merch-item[data-missing-keys*="salePrice"] .mt-price {
    text-decoration: none;
    margin-left: 0;
    color: #575353;
    font-weight: normal;
    display: inline-block;
}

#MONETATE-ID .mt-label {
    font-family: 'DINPro-Regular', 'Helvetica Neue', Helvetica, Arial, sans-serif;
    font-size: 1.2rem;
    color: #575353;
    display: none;
}

@media only screen and (max-width: 767px) {
    #MONETATE-ID .mt-swatch-images>img {
        border: .1rem solid #FFF;
        border-radius: 100%;
        box-shadow: 0 0 0 .1rem #d6d6d6 !important;
        width: 21px;
        margin-right: 8px;
    }
}

@media only screen and (min-width: 768px) {
    #MONETATE-ID .mt-title {
        font-size: 2.4rem;
    }
    #MONETATE-ID .mt-swatch-images,
    #MONETATE-ID .mt-merch-item:hover .mt-label {
        display: none;
    }
    #MONETATE-ID .mt-label,
    #MONETATE-ID .mt-merch-item:hover .mt-swatch-images {
        display: block;
    }
    #MONETATE-ID .mt-merch-item:hover {
        padding-bottom: 10px !important;
    }
    #MONETATE-ID .mt-swatch-images>img {
        width: 40px;
        margin: 0 10px;
    }
}

