/* ===================================================== */
/* DEFAULT SLIDER STYLES
/* Edit at your own risk!
/* ===================================================== */
#MONETATE-ID {
  position: relative;
}

#MONETATE-ID li {
  list-style: none;
}

#MONETATE-ID [data-slider-mask] {
  margin: 0 !important;
  overflow: hidden !important;
  padding: 0 !important;
  position: relative !important;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider-mask], #MONETATE-ID[data-slider-wrapper="fade"] [data-slider-mask] {
  touch-action: pan-y;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  touch-action: pan-x;
}

#MONETATE-ID [data-slider] {
  display: block;
  margin: 0 !important;
  padding: 0 !important;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider] {
  white-space: nowrap !important;
}

#MONETATE-ID [data-slide] {
  float: none;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slide] {
  display: inline-block !important;
  vertical-align: top;
  white-space: normal;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slide] {
  display: block !important;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide] {
  opacity: 0;
  position: absolute;
  top: 0;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide][data-slide-active] {
  opacity: 1;
  position: relative;
  z-index: 1;
}

#MONETATE-ID [data-pagination-slide],
#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
  cursor: pointer;
  z-index: 1;
}

#MONETATE-ID [data-pagination-slide] * {
  pointer-events: none;
}

/* ===================================================== */
/* CUSTOM SLIDER STYES
/* ===================================================== */
#MONETATE-ID {
  text-align: center;
}

#MONETATE-ID .mt_slider__header {
  margin-left: 12px;
  text-align: left;
}

#MONETATE-ID .mt_slider__wrapper {
  position: relative;
}

#MONETATE-ID [data-slider-mask] {
  display: inline-block;
  width: calc(100% - 64px);
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  height: 400px !important;
}

#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
  -moz-user-select: none;
  -ms-user-select: none;
  -o-user-select: none;
  -webkit-user-select: none;
  background: none;
  color: #000;
  font-size: 25px;
  height: 32px;
  padding: 0;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  user-select: none;
  width: 32px;
}

#MONETATE-ID [data-prev-button] {
  left: 0;
}

#MONETATE-ID [data-next-button] {
  right: 0;
}

#MONETATE-ID [data-pagination="thumbnails"] [data-pagination-slide] {
  background: none;
  border-radius: 0;
  height: auto;
  width: 40px;
}

#MONETATE-ID [disabled] {
  opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide] {
  background: #000;
  border-radius: 6px;
  display: inline-block;
  height: 12px;
  margin: 4px;
  opacity: 0.5;
  width: 12px;
}

#MONETATE-ID [data-pagination-slide][data-slide-active] {
  opacity: 1;
}

#MONETATE-ID [data-pagination*="dots"] [data-pagination-slide] {
  /* remove below for pagination numbers */
  text-indent: -9999px;
}

#MONETATE-ID [data-slide] > div {
  padding: 0 6px 4px;
  text-align: left;
}

#MONETATE-ID [data-slide] img {
  max-width: 100%;
}

#MONETATE-ID [data-slide] a {
  text-decoration: none;
}

#MONETATE-ID .mt_slide__img {
  padding: 5px;
}

#MONETATE-ID .mt_slide__text {
  color: #282828;
  font-family: Nunito\ Sans;
}

#MONETATE-ID .mt_slide__brand {
  font-size: 12px;
  font-weight: 400;
  line-height: 19px;
  margin: 0;
  max-height: 19px;
  overflow: hidden;
  padding: 0;
  text-transform: capitalize;
}

#MONETATE-ID .mt_slide__title {
  color: #000;
  font-size: 14px;
  line-height: 18px;
  margin: 0;
  max-height: 36px;
  overflow-wrap: break-word;
  padding: 0;
  position: relative;
  text-transform: capitalize;
  white-space: normal;
}

#MONETATE-ID .mt_slide__price {
  color: #000;
  font-size: 14px;
  font-weight: 800;
  line-height: 24px;
}

#MONETATE-ID .mt_slide__price--reg {
  color: rgba(0, 0, 0, 0.6);
  font-size: 12px;
  text-decoration: line-through;
}

#MONETATE-ID [data-missing-keys*="salePrice"] .mt_slide__price--reg {
  color: #000;
  font-size: 14px;
  text-decoration: none;
}
