@charset "UTF-8";
.mt_closable-banner {
  background: #252525;
  color: #fff;
  display: block;
}

.mt_closable-banner__inner {
  display: block;
  margin: 0 auto;
  max-width: 1920px;
  padding: 0 20px;
  position: relative;
}

@media screen and (min-width: 1280px) {
  .mt_closable-banner__inner {
    padding: 0 42px;
  }
}

.mt_closable-banner__text {
  font-family: 'MaisonNeue-Bold', Arial, sans-serif;
  font-size: 11px;
  line-height: 1.5;
  padding: 13px 0 9px;
  position: relative;
  text-align: center;
  text-transform: uppercase;
  white-space: nowrap;
}

@media screen and (min-width: 768px) {
  .mt_closable-banner__text {
    font-size: 12px;
    padding: 13px 0 12px;
  }
}

@media screen and (min-width: 1280) {
  .mt_closable-banner__text {
    padding: 18px 0 17px;
  }
}

.mt_closable-banner__close {
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
  padding: 0;
  position: absolute;
  right: 20px;
  top: 50%;
  -webkit-transform: translateY(-50%);
          transform: translateY(-50%);
}

@media screen and (min-width: 1280px) {
  .mt_closable-banner__close {
    right: 42px;
  }
}

.mt_closable-banner__close::before {
  -moz-osx-font-smoothing: grayscale;
  -webkit-font-smoothing: antialiased;
  content: "î€¶";
  display: inline-block;
  font-family: iconfont;
  font-size: 16px;
  font-style: normal;
  font-variant: normal;
  font-weight: 400;
  line-height: 1;
  speak: none;
  text-decoration: none;
  text-rendering: optimizeLegibility;
  text-transform: none;
  vertical-align: middle;
}
