value.content:
#checkout > a.mini-cart-action:before {
  text-align: center;
  width: 100%;
}

#secureCheckout > a.mini-cart-action {
  font-size: 15px !important;
  line-height: 22px !important;
}

#checkout {
  margin-right: 20px;
  text-align: right;
}

#secureCheckout {
  text-align: left;
}

.mini-cart-actions.cart-actions {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: horizontal;
  -webkit-box-direction: normal;
      -ms-flex-direction: row;
          flex-direction: row;
  -ms-flex-wrap: nowrap;
      flex-wrap: nowrap;
  -webkit-box-pack: center;
      -ms-flex-pack: center;
          justify-content: center;
}

.mini-cart-actions.cart-actions > * {
  display: block;
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
}

.cart-list .cart-actions-item {
  padding-left: 0 !important;
  padding-right: 0 !important;
}

.topnav-minicart-panel .mini-cart-action {
  width: 80% !important;
}
