.mt_countdown {
  box-sizing: border-box;
  display: block;
  float: none;
  margin: 12px auto;
  padding: 12px 0;
  position: relative;
  width: 90%;
}

.mt_countdown * {
  box-sizing: border-box;
}

.mt-countdown {
  background-image: url({{BKG_URL}});
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  display: block;
  height: 0;
  padding-bottom: calc((798 / 2572) * 100%);
  position: relative;
  width: 100%;
}

.mt-countdown__timer {
  -ms-flex-line-pack: center;
      align-content: center;
  -webkit-box-align: center;
      -ms-flex-align: center;
          align-items: center;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  font-variant: tabular-nums;
  -webkit-box-pack: justify;
      -ms-flex-pack: justify;
          justify-content: space-between;
  left: 39%;
  position: absolute;
  right: 15%;
  top: 54%;
  -webkit-transform: translate(0%, -50%);
          transform: translate(0%, -50%);
}

.mt_tickerElem {
  display: block;
  position: relative;
  text-align: center;
}

.mt_tickerElem .mt_time {
  color: #fff;
  font-size: 6vw;
  font-weight: bold;
}

.mt_tickerElem .mt_suffix {
  color: #fff;
  font-size: 1.5vw;
  font-weight: bold;
  left: 50%;
  position: absolute;
  text-transform: uppercase;
  top: 100%;
  -webkit-transform: translate(-50%, 0);
          transform: translate(-50%, 0);
}
