/**
 * DEFAULT SLIDER STYLES
 * Edit at your own risk!
 */
#MONETATE-ID {
  position: relative;
}

#MONETATE-ID [data-slider-mask] {
  overflow: hidden!important;
  position: relative!important;
  padding: 0!important;
  margin: 0!important;
}

#MONETATE-ID li {
  list-style: none;
}

#MONETATE-ID [data-slider] {
  padding: 0!important;
  margin: 0!important;
  display: block;
}

#MONETATE-ID [data-slide] {
  float: none;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider] {
  white-space: nowrap!important;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slide] {
  display: inline-block!important;
  white-space: normal;
  vertical-align: top;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide] {
  position: absolute;
  top: 0;
  opacity: 0;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide][data-slide-active] {
  position: relative;
  z-index: 1;
  opacity: 1;
}

#MONETATE-ID [data-pagination-slide],
#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button]{
  cursor: pointer;
  z-index: 1;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider-mask],
#MONETATE-ID[data-slider-wrapper="fade"] [data-slider-mask] {
  touch-action: pan-y;
}

#MONETATE-ID [data-pagination-slide] * {
  pointer-events: none;
}

/** ----END DEFAULT STYLES---- */


/**
 * CUSTOM SLIDER STYLING
 */

#MONETATE-ID {
  text-align: center;
  width: 100%;
  margin-top: 2.1875rem;
}

#MONETATE-ID [data-pagination="thumbnails"] [data-pagination-slide] {
  border-radius: 0;
  background: none;
  width: 40px;
  height: auto;
}

#MONETATE-ID [disabled] {
  opacity: .35;
  cursor: auto;
  pointer-events: none;
}

#MONETATE-ID [data-pagination-slide] {
  display: inline-block;
  width: 12px;
  height: 12px;
  background: #000;
  margin: 4px;
  border-radius: 6px;
  opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide][data-slide-active] {
  opacity: 1;
}

#MONETATE-ID [data-slide] > div {
  padding: 0 12px;
  height: 100%;
}

#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
  top: 45%;
  position: absolute;
  font-size: 25px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  -o-user-select: none;
  user-select: none;
  padding: 0;
  background: none;
  color: #000;
}

#MONETATE-ID [data-prev-button] {
  left: -4.4%;
}

#MONETATE-ID [data-next-button] {
  right: -4.4%;
}

#MONETATE-ID [data-slider-mask] {
  display: inline-block;
  width: calc(100% + 10px);
  margin-left: -5px !important;
}

#MONETATE-ID [data-slide] img {
  max-height: 100%;
  width: 100%;
}

#MONETATE-ID [data-slide] a {
  text-decoration: none;
}

#MONETATE-ID [data-pagination*="dots"] [data-pagination-slide] {
  /* remove below for pagination numbers */
  text-indent: -9999px;
}

#MONETATE-ID h2 {
  font-family: freightdisp_prolight;
  font-size: 26px;
  letter-spacing: 0;
  line-height: 30px;
  margin-bottom: 30px;
}

#MONETATE-ID .mt-product-image {
  position: relative;
  margin-bottom: 1.25rem;
}

#MONETATE-ID .mt-text-container {
  text-align: center;
}

#MONETATE-ID .mt-brand {
  text-transform: uppercase;
}

#MONETATE-ID .product-name {
  text-align: center !important;
  font-size: 15px;
}

#MONETATE-ID .product-pricing {
  margin: 1.125rem 0 0;
}

#MONETATE-ID .product-standard-price {
  text-decoration: line-through;
  color: #aaa;
  font-size: 15px;
}

#MONETATE-ID .product-sales-price {
  font-size: 15px;
  color: #d22630;
}

#MONETATE-ID [data-missing-keys*="salePrice"] .product-standard-price {
  color: #000;
  text-decoration: none;
}

#MONETATE-ID [data-prev-button], 
#MONETATE-ID [data-next-button] {
  border: none;
  outline: 0;
  position: absolute;
  top: 39.5%;
  width: 1.5625rem;
  height: 1.5625rem;
  background: inherit;
  text-decoration: none;
  text-align: center;
  z-index: 1;
  font-family: cm-icon;
  font-size: 25px;
}

#MONETATE-ID [data-prev-button]:after {
  content: "";
}

#MONETATE-ID [data-next-button]:after {
  content: "";
}

@media only screen and (max-width: 959px) {
  #MONETATE-ID {
    overflow: hidden;
  }
  #MONETATE-ID [data-slide] > div {
    padding: 0 5px;
  }
  #MONETATE-ID [data-prev-button] {
    display: none;
  }
  #MONETATE-ID [data-next-button] {
    display: none;
  }
}

@media screen and (min-width: 768px) {
  #MONETATE-ID [data-slider-mask] {
    width: 100%;
    margin-left: 0 !important;
  }
  #MONETATE-ID {
    margin: auto;
    max-width: 41.125rem;
  }
}

@media screen and (min-width: 960px) {
  #MONETATE-ID {
    max-width: 53.5rem;
    margin-top: 5.125rem;
  }
  #MONETATE-ID .mt-product-image {
    margin-bottom: 1.8125rem;
  }
}

@media screen and (min-width: 1180px) {
  #MONETATE-ID {
    max-width: 63rem;
  }
}

@media only screen and (width: 960px) {
  #MONETATE-ID {
    max-width: 52.325rem !important;
  }
  #MONETATE-ID [data-slide] > div {
    padding: 0 5px !important;
  }
}

#ymal {
  display: none;
}
