/**
 * DEFAULT SLIDER STYLES
 * Edit at your own risk!
 */
#MONETATE-ID {
  position: relative;
}

#MONETATE-ID [data-slider-mask] {
  overflow: hidden!important;
  position: relative!important;
  padding: 0!important;
  margin: 0!important;
}

#MONETATE-ID li {
  list-style: none;
}

#MONETATE-ID [data-slider] {
  padding: 0!important;
  margin: 0!important;
  display: block;
}

#MONETATE-ID [data-slide] {
  float: none;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider] {
  white-space: nowrap!important;
}

#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slide] {
  display: inline-block!important;
  white-space: normal;
  vertical-align: top;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slide] {
  display: block!important;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide] {
  position: absolute;
  top: 0;
  opacity: 0;
}

#MONETATE-ID[data-slider-wrapper="fade"] [data-slide][data-slide-active] {
  position: relative;
  z-index: 1;
  opacity: 1;
}

#MONETATE-ID [data-pagination-slide],
#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button]{
  cursor: pointer;
  z-index: 1;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  touch-action: pan-x;
}
#MONETATE-ID[data-slider-wrapper="horizontal"] [data-slider-mask],
#MONETATE-ID[data-slider-wrapper="fade"] [data-slider-mask] {
  touch-action: pan-y;
}

#MONETATE-ID [data-pagination-slide] * {
  pointer-events: none;
}

/** ----END DEFAULT STYLES---- */


/**
 * CUSTOM SLIDER STYLING
 */
#MONETATE-ID .mt-slider-container {
  max-width: 1192px;
}

#MONETATE-ID [data-pagination="thumbnails"] [data-pagination-slide] {
  border-radius: 0;
  background: none;
  width: 40px;
  height: auto;
}

#MONETATE-ID [disabled] {
  opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide] {
  display: inline-block;
  width: 12px;
  height: 12px;
  background: #000;
  margin: 4px;
  border-radius: 6px;
  opacity: 0.5;
}

#MONETATE-ID [data-pagination-slide][data-slide-active] {
  opacity: 1;
}

#MONETATE-ID [data-slide] > div {
  padding: 10px;
  height: 100%;
}


#MONETATE-ID [data-prev-button],
#MONETATE-ID [data-next-button] {
  top: 45%;
  position: absolute;
  font-size: 25px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  -o-user-select: none;
  user-select: none;
  padding: 0;
  background: none;
  color: #000;
}


#MONETATE-ID [data-prev-button] {
  margin-left: -40px;
}

/*
#MONETATE-ID [data-next-button] {
  right: 10px;
}
*/

#MONETATE-ID[data-slider-wrapper="vertical"] [data-prev-button] {
  top: 0;
  left: 50%;
  transform: translateX(-50%);
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-next-button]{
  top: auto;
  bottom: 0;
  left: 50%;
  right: auto;
  transform: translateX(-50%);
}

#MONETATE-ID [data-slider-mask] {
  display: inline-block;
  width: calc(100% - 106px);
  max-width: 1072px;
  max-height: 462px;
}

#MONETATE-ID {
  text-align: center;
  padding: 6.25rem 8% 0 15%;
}

#MONETATE-ID[data-slider-wrapper="vertical"] [data-slider-mask] {
  height: 400px!important;
}

#MONETATE-ID [data-slide] img {
  max-height: 100%;
  width: 100%;
}

/*
#MONETATE-ID [data-slide] a {
  text-decoration: none;
}
*/

#MONETATE-ID [data-pagination*="dots"] [data-pagination-slide] {
  /* remove below for pagination numbers */
  text-indent: -9999px;
}

/*
#MONETATE-ID .mt-text-container {
  text-align: left;
  padding: 0 10px;
}
*/

#MONETATE-ID h2 {
  width: 100%;
  font-family: didot w01 italic;
  font-style: Normal;
  color: #474747;
  font-size: 1.5rem;
  line-height: 1em;
  letter-spacing: .05rem;
  text-transform: capitalize;
}

#MONETATE-ID .mt-brand {
  text-transform: uppercase;
}

#MONETATE-ID .mt-text-container > div {
  overflow: visible;
  max-width: 14.375rem;
  padding: 0 .625rem;
  margin: .1875rem 0 0;
  line-height: 11px;
  text-align: left;
  font-size: .6875rem;
}

#MONETATE-ID .mt-text-container > a:hover {
  text-decoration: underline;
}

#MONETATE-ID .mt-alt-price {
  color: #ef001b;
  font-family: hco gotham bold;
  font-style: Normal;
  font-weight: 500;
}

#MONETATE-ID .mt-black {
  color: #051e39;
}

#MONETATE-ID .mt-price {
  color: #051e39;
  font-family: hco gotham bold;
  font-style: Normal;
  font-weight: 500;
}

#MONETATE-ID [data-prev-button], #MONETATE-ID [data-next-button] {
  border: none;
  position: absolute;
  width: 1.875rem;
  height: 1.875rem;
  background: inherit;
  text-decoration: none;
  text-align: center;
  z-index: 1;
  font: normal normal normal 24px/1 FontAwesome;
  color: #474747;
}

#MONETATE-ID [data-prev-button]:after {
  content: "\f104";
}

#MONETATE-ID [data-next-button]:after {
  content: "\f105";
}

@media only screen and (max-width: 959px) {
  #MONETATE-ID {
    text-align: center;
    padding: 6.25rem 0 0 0;
  }

  #MONETATE-ID [data-slider-mask]  {
    width: 100%;
  }

  #MONETATE-ID [data-prev-button] {
    display: none;
  }

  #MONETATE-ID [data-next-button] {
    display: none;
  }

  #MONETATE-ID .mt-text-container > div {
    font-weight: 400;
    font-size: .5625rem;
  }

}

#ymal {
  display: none;
}
