value.content:
.mt-hero {
  display: block;
  margin: 0 auto;
  position: relative;
}

.mt-hero__picture {
  overflow: hidden;
  position: relative;
}
.mt-hero__picture img {
  width: 100%;
}

/* ===================================================== */
/* Overlay
/* ===================================================== */
.mt-hero__overlay {
  background: #fff;
  margin: 0 auto;
  max-width: 1440px;
  padding: 0.625rem;
  position: relative;
  text-align: center;
  width: 100%;
}

@media screen and (min-width: 1024px) {
  .mt-hero__overlay {
    background: transparent;
    padding: 0;
    position: absolute;
    top: 50%;
    -webkit-transform: translateY(-50%);
            transform: translateY(-50%);
    width: auto;
  }
}

/* ===================================================== */
/* Hide/Show Mobile Overlay
/* ===================================================== */
.mt-hero__overlay[data-show-mobile="true"] {
  display: block;
}
.mt-hero__overlay[data-show-mobile="false"] {
  display: none;
}


/* ===================================================== */
/* Hide/Show Tablet Overlay
/* ===================================================== */
@media screen and (min-width: 640px) {
  .mt-hero__overlay[data-show-tablet="true"] {
    display: block;
  }
  .mt-hero__overlay[data-show-tablet="false"] {
    display: none;
  }
}


/* ===================================================== */
/* Hide/Show Desktop Overlay
/* ===================================================== */
@media screen and (min-width: 1024px) {
  .mt-hero__overlay[data-show-desktop="true"] {
    display: block;
  }
  .mt-hero__overlay[data-show-desktop="false"] {
    display: none;
  }
}


/* ===================================================== */
/* Position Overlay - Tablet
/* ===================================================== */
@media screen and (min-width: 640px) {
  .mt-hero__overlay.tablet-left {
    left: 0;
    right: auto;
    text-align: left;
  }
  .mt-hero__overlay.tablet-right {
    left: auto;
    right: 0;
    text-align: right;
  }
}


/* ===================================================== */
/* Position Overlay - Desktop
/* ===================================================== */
@media screen and (min-width: 1024px) {
  .mt-hero__overlay.desktop-left {
    left: 20px;
    right: auto;
    text-align: left;
  }
  .mt-hero__overlay.desktop-right {
    left: auto;
    right: 20px;
    text-align: right;
  }
}


/* ===================================================== */
/* Text Styling
/* ===================================================== */
.mt-hero__overlay h2 {
  color: #000;
  display: block;
  font-size: calc( 36px + (50 - 36) * (100vw - 320px) / (1440 - 320));
  font-weight: 700;
  letter-spacing: 0.025em;
  line-height: 1.0em;
  margin: 0;
  padding: 0;
  text-transform: uppercase;
}

.mt-hero__overlay h3 {
  display: inline;
  font-size: calc( 1.75em + (4.5 - 1.75) * (100vw - 20em) / (120 - 20));
  letter-spacing: 0.025em;
  line-height: 1.35em;
  padding: 0px 10px;
}

.mt-hero__overlay h4 {
  color: #000;
  display: block;
  font-family: Helvetica, Helvetica Neue,Arial,sans-serif;
  font-size: calc( 12px + (18 - 12) * (100vw - 320px) / (1920 - 320));
  font-weight: 300;
  line-height: 1.4em;
  margin: 5px 0 15px;
  text-transform: none;
}
